import React from 'react';
import styled from 'styled-components';
import { Link, useNavigate } from 'react-router-dom';
import { useDarkMode } from '../index';

// Top bar
const TopBar = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 1rem;
  padding: 18px 0 0 0;
  max-width: 1300px;
  margin: 0 auto;
  background: ${({ theme }) => theme.background};
`;
const TopLeft = styled.div`
  color: ${({ theme }) => theme.text};
  font-weight: 600;
  display: flex;
  align-items: center;
  gap: 12px;
`;
const Epaper = styled.span`
  color: ${({ theme }) => theme.accent};
  font-weight: 700;
  margin-left: 6px;
`;
const TopRight = styled.div`
  display: flex;
  align-items: center;
  gap: 24px;
`;
const Account = styled.div`
  color: ${({ theme }) => theme.text};
  font-weight: 600;
  display: flex;
  align-items: center;
  gap: 4px;
`;
const SubscribeBtn = styled.button`
  background: ${({ theme }) => theme.accent};
  color: #fff;
  font-weight: 700;
  border: none;
  border-radius: 6px;
  padding: 10px 28px;
  font-size: 1.1rem;
  cursor: pointer;
`;

// Logo
const LogoRow = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 0 auto;
  padding: 0;
  background: ${({ theme }) => theme.background};
`;
const LogoImg = styled.img`
  height: 70px;
  margin: 0 auto;
  filter: ${({ theme }) => theme.background === '#181a1b' ? 'invert(1) grayscale(1)' : 'none'};
`;

// Nav bar
const NavBar = styled.nav`
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0;
  border-bottom: 1px solid ${({ theme }) => theme.cardBorder};
  margin-top: 10px;
  background: ${({ theme }) => theme.background};
`;
const NavInner = styled.div`
  display: flex;
  align-items: center;
  width: 100%;
  max-width: 1300px;
  margin: 0 auto;
  padding: 0 16px;
`;
const NavLeft = styled.div`
  display: flex;
  align-items: center;
  gap: 18px;
  min-width: 180px;
`;
const Hamburger = styled.button`
  background: none;
  border: none;
  font-size: 2rem;
  cursor: pointer;
  display: flex;
  align-items: center;
  color: ${({ theme }) => theme.text};
